import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export const apiClient = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

export interface Project {
  id: number;
  status: string;
  companyName: string;
  siteName: string;
  equipment: string; // JSON文字列
  clientContact?: string;
  photographer?: string;
  shootPeriod?: string;
  startDate?: string;
  endDate?: string;
  drawingModel?: string;
  remarks?: string;
  siteAddress?: string;
  createdAt: string;
  updatedAt: string;
  createdBy: string;
  updatedBy: string;
}

// プロジェクト API
export const projectApi = {
  getAll: () => apiClient.get<Project[]>('/api/projects'),
  getById: (id: number) => apiClient.get<Project>(`/api/projects/${id}`),
  create: (data: Omit<Project, 'id' | 'createdAt' | 'updatedAt'>) => 
    apiClient.post<Project>('/api/projects', data),
  update: (id: number, data: Partial<Project>) => 
    apiClient.put<Project>(`/api/projects/${id}`, data),
  delete: (id: number) => apiClient.delete(`/api/projects/${id}`),
};
