import React, { useState } from 'react';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { apiClient } from '../../services/api';

interface ImportDialogProps {
    isOpen: boolean;
    onClose: () => void;
}

const ImportDialog: React.FC<ImportDialogProps> = ({ isOpen, onClose }) => {
    const [file, setFile] = useState<File | null>(null);
    const [isProcessing, setIsProcessing] = useState(false);
    const queryClient = useQueryClient();

    const importMutation = useMutation({
        mutationFn: async (importData: any[]) => {
            const response = await apiClient.post('/api/projects/import', { projects: importData });
            return response.data;
        },
        onSuccess: () => {
            queryClient.invalidateQueries({ queryKey: ['projects'] });
            onClose();
            alert('インポートが完了しました');
        },
        onError: (error) => {
            console.error('Import error:', error);
            alert('インポートに失敗しました');
        },
    });

    const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        const selectedFile = event.target.files?.[0];
            if (selectedFile && selectedFile.name.endsWith('.xlsx')) {
                setFile(selectedFile);
            } else {
                alert('Excelファイル(.xlsx)を選択してください');
            }
    };

    const handleImport = async () => {
        if (!file) {
            alert('ファイルを選択してください');
            return;
        }

        setIsProcessing(true);
    
        try {
            // ファイルを読み込んでJSON形式に変換
            const reader = new FileReader();
            reader.onload = async (e) => {
                try {
                    const data = new Uint8Array(e.target?.result as ArrayBuffer);
            
                    // SheetJSでExcelファイルを解析
                    const XLSX = await import('xlsx');
                    const workbook = XLSX.read(data, { type: 'array' });
                    const worksheet = workbook.Sheets['管理表'];
                
                    if (!worksheet) {
                        alert('管理表シートが見つかりません');
                        setIsProcessing(false);
                        return;
                    }

                    // Excelデータを配列に変換
                    const jsonData: any[][] = XLSX.utils.sheet_to_json(worksheet, { 
                        header: 1,
                        defval: ''
                    });

                    // ヘッダー行をスキップしてデータを処理
                    const projects = jsonData.slice(2).filter((row: any) => row[2] && row[3]).map((row: any) => ({
                        進捗状況: row[1] || '未見積',
                        会社名: row[2],
                        現場名: row[3],
                        機材: JSON.stringify(row[4] ? [row[4]] : []),
                        撮影担当: row[6],
                        撮影期間: row[7],
                        撮影開始日: row[8] ? new Date(row[8]).toISOString().split('T')[0] : null,
                        撮影終了日: row[9] ? new Date(row[9]).toISOString().split('T')[0] : null,
                        備考: row[11],
                        現場住所: row[12]
                    }));

                if (projects.length === 0) {
                    alert('インポート可能なデータが見つかりませんでした');
                    setIsProcessing(false);
                    return;
                }

                importMutation.mutate(projects);
            } catch (error) {
                console.error('File processing error:', error);
                alert('ファイルの処理に失敗しました');
                setIsProcessing(false);
            }
        };
      
            reader.readAsArrayBuffer(file);
        } catch (error) {
            console.error('File processing error:', error);
            alert('ファイルの処理に失敗しました');
            setIsProcessing(false);
        }
    };

    if (!isOpen) return null;

    return (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
            <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
                <div className="mt-3">
                    <h3 className="text-lg font-semibold text-gray-900 mb-4">
                        Excelデータインポート
                    </h3>
          
                    <div className="space-y-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Excelファイルを選択
                            </label>
                            <input
                                type="file"
                                accept=".xlsx"
                                onChange={handleFileChange}
                                className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                            />
                        </div>
            
                        {file && (
                            <div className="text-sm text-gray-600">
                                選択ファイル: {file.name}
                            </div>
                        )}
            
                        <div className="text-xs text-gray-500">
                            ※ 管理表シートのデータをインポートします
                        </div>
                    </div>

                    <div className="flex justify-end space-x-3 mt-6">
                        <button
                            onClick={onClose}
                            disabled={isProcessing}
                            className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50 disabled:opacity-50"
                        >
                            キャンセル
                        </button>
                        <button
                            onClick={handleImport}
                            disabled={!file || isProcessing}
                            className="px-4 py-2 border border-transparent rounded-md text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 disabled:opacity-50"
                        >
                            {isProcessing ? '処理中...' : 'インポート実行'}
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ImportDialog;
